mapper.insert:
insert into sysgroup (GroupCode, ComLevel, ComType, 
  GroupName, ValidStatus, CreateTime, 
  CreateUserCode, ModifyTime, ModifyUserCode, 
  Reverse1, Reverse2, Reverse3
  )
values (#{groupCode,jdbcType=VARCHAR}, #{comLevel,jdbcType=VARCHAR}, #{comType,jdbcType=VARCHAR}, 
  #{GroupName,jdbcType=VARCHAR}, #{validStatus,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUserCode,jdbcType=VARCHAR}, #{modifyTime,jdbcType=TIMESTAMP}, #{modifyUserCode,jdbcType=VARCHAR}, 
  #{reverse1,jdbcType=VARCHAR}, #{reverse2,jdbcType=VARCHAR}, #{reverse3,jdbcType=VARCHAR}
  )

mapper.select:
select * from 
sysgroup sg 
where 1=1 
and sg.comLevel =#{comLevel} 
and sg.comType =#{comType}

    select * from sysgroup sg where 1=1

		AND sg.groupCode like '%${groupCode}%' 


	   AND sg.groupName like '%${groupName}%' 


	 	  AND sg.comLevel =#{comLevel}  


	 	  AND sg.comType =#{comType}  



    order by sg.CreateTime Desc
