mapper.select:
select 

from sysusergroup
where UserCode = #{userCode,jdbcType=VARCHAR}
  and GroupCode = #{groupCode,jdbcType=VARCHAR}

mapper.delete:
delete from sysusergroup
where UserCode = #{userCode,jdbcType=VARCHAR}
  and GroupCode = #{groupCode,jdbcType=VARCHAR}

mapper.update:
update sysusergroup
set ValidStatus = #{validStatus,jdbcType=VARCHAR},
  CreateTime = #{createTime,jdbcType=TIMESTAMP},
  CreateUserCode = #{createUserCode,jdbcType=VARCHAR},
  Reverse1 = #{reverse1,jdbcType=VARCHAR},
  Reverse2 = #{reverse2,jdbcType=VARCHAR},
  Reverse3 = #{reverse3,jdbcType=VARCHAR}
where UserCode = #{userCode,jdbcType=VARCHAR}
  and GroupCode = #{groupCode,jdbcType=VARCHAR}

mapper.select:
   	select  

	     from sysusergroup sug where 1=1

	 	  AND sug.groupCode =#{groupCode}
